package com.class7;

public class CompoundOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int c=100;
		c+=100; //c=200
		System.out.println(c);
		
		c++;//c=201
		System.out.println(c);
		
		int d=30;
		d%=7;// remainder 2
		System.out.println(d);
		
		
	}

}
