package com.class9;

public class NestedLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 5; i++) {
			for (int y = 1; y < 6; y++) {
				System.out.print(y);
			}
			System.out.println();
		}
		for (int q = 1; q < 5; q++) {
			for (int w = 1; w <= 5; w++) {
				System.out.print("*");

			}
			System.out.println();
		}

		for (int e = 1; e < 5; e++) {
			for (int r = 5; r > 0; r--) {
				System.out.print(r);
			}
			System.out.println();
		}
		for (int t = 1; t <=4; t++) {
			for(int p=1;p<=t;p++) {
			System.out.print("*");
			}
			System.out.println();

		}

	}
}
